const cookiesGrid = document.getElementById('cookiesGrid');

// Function to display cookie recipes
function displayCookieRecipes(recipes) {
    cookiesGrid.innerHTML = '';
    recipes.forEach(recipe => {
        const card = document.createElement('div');
        card.className = 'cookie-card';
        card.innerHTML = `
            <img src="${recipe.image}" alt="${recipe.title}">
            <h2>${recipe.title}</h2>
            <p>${recipe.description}</p>
        `;
        cookiesGrid.appendChild(card);
    });
}

// Initial display of all cookie recipes
displayCookieRecipes(cookieRecipes);
